import { RuleTester } from "@typescript-eslint/rule-tester";
import noWhereClause from "../rules/no-where-clause.js";

const ruleTester = new RuleTester({
  languageOptions: {
    parserOptions: { parser: "@typescript-eslint/parser" },
  },
});

ruleTester.run("my-rule", noWhereClause, {
  valid: [
    "SELECT(req.subject)",
    "SELECT.one.from(Books)",
    "SELECT.from(Books).where({ ID: 12345 })",
  ],
  invalid: [
    {
      code: "SELECT.from(Books)",
      errors: [{ messageId: "missingWhere" }],
    },
  ],
});
